import React, { Component } from 'react';
import Cell from './Cell';

export default class Board extends Component {
	state = {
    currentGrid: [ {} ],
    players: [{id: 1}, {id: 2}]
	};

	componentDidMount() {
		this.generateGrid();
	}

	getRandomInt(min, max) {
		return Math.floor(Math.random(min) * Math.floor(max));
	}

	get2RandCellIndexes() {
		const indexes = [];
		do {
			const nb = this.getRandomInt(1, 98);
			if (!indexes.includes(nb)) indexes.push(nb);
		} while (indexes.length !== 2);
		return indexes;
	}

	generateGrid() {
		const grid = [];
		const indexes = this.get2RandCellIndexes();
		var count = 0;
		// const player1 = this.state.currentGrid[0];

		for (let i = 0; i <= 9; i++) {
			for (let j = 0; j <= 9; j++) {
				const pos = indexes.indexOf(count); // if not found => return -1
				if (count === 0 || count === 99) {
					grid.push({
						x: i,
						y: j,
						taken: true,
						color: null,
						player: this.state.players[count === 0 ? 0 : 99],
						meteorite: false
					});
				} else {
					grid.push({
						x: i,
						y: j,
						taken: pos !== -1 ? true : false,
						color: null,
						meteorite: pos !== -1 ? true : false
					});
				}
				count++; // gui@check ça suis pas sûr
			}
		}
		this.setState({ currentGrid: grid });
	}

	generateDinos() {}

	render() {
		return (
			<div className="board">
				{this.state.currentGrid.length &&
					this.state.currentGrid.map((cell, i) => {
						return <Cell key={i} cell={cell} />;
					})}
			</div>
		);
	}
}
