import React, { Component } from "react";
import Cell from "./Cell";

export default class Board extends Component {
  state = {
    currentGrid: [{}]
  };

  componentDidMount() {
    this.generateGrid();
  }

  generateGrid() {
    const grid = [{ x: 0, y: 0, takken: false, color: null, cellNumber: 0 }];
    for (let i = 0; i <= 9; i++) {
      console.log(i);

      for (let j = 0; j <= 9; j++) {
        console.log(j);
        grid.push({
          x: [i],
          y: [j],
          taken: false,
          color: null,
          cellNumber: [i]
        });
      }
    }

    this.setState({ currentGrid: grid });
  }

  // function generateBoard() {

  // }

  render() {
    return (
      <div className="board">
        <button onClick={() => this.generateGrid()}> coucou</button>
        {this.state.currentGrid.length &&
          this.state.currentGrid.map((cell, i) => {
            return <Cell key={i} />;
          })}
      </div>
    );
  }
}
