import React, { Component } from "react";
import Cell from "./Cell";

export default class Board extends Component {
  state = {
    currentGrid: [{}]
  };

  componentDidMount() {
    this.generateGrid();
  }

  getRandomInt(min, max) {
    return Math.floor(Math.random(min) * Math.floor(max));
  }

  generateMeteor() {
    var res = 0;
    let randomCell = this.state.currentGrid[this.getRandomInt(1, 98)];
    let index = this.state.currentGrid.indexOf(randomCell);
    console.log("index ?", index);
    if (randomCell && randomCell.type !== "meteorite") {
      const copy = [...this.state.currentGrid];
      copy[index] = {
        taken: true,
        type: "meteorite",
        color: null
      };
      res = 1;

      this.setState({ currentGrid: copy }, () => {
        console.log("yata");
        const meteor = this.state.currentGrid.filter(
          el => el.type === "meteorite"
        );
        console.log(meteor.length);
      });
    }
    return res;
  }

  get2RandCellIndexes() {
    const indexes = [];
    do {
      const nb = this.getRandomInt(1, 98);
      if (!indexes.includes(nb)) indexes.push(nb);
    } while (indexes.length !== 2);
    return indexes;
  }

  generateGrid() {
    const grid = [];
    const indexes = this.get2RandCellIndexes();
    console.log(indexes);
    var count = 0;
    var meteors = 0;
    for (let i = 0; i <= 9; i++) {
      for (let j = 0; j <= 9; j++) {
        count++;
        console.log(indexes[0] === count || indexes[1] === count);
        grid.push({
          x: i,
          y: j,
          taken: false,
          color: null,
          cellNumber: [i]
        });
      }
    }

    //generate meteorites

    // console.log(randomCell);

    this.setState({ currentGrid: grid }, () => {});
  }

  // function generateBoard() {

  // }

  render() {
    return (
      <div className="board">
        {this.state.currentGrid.length &&
          this.state.currentGrid.map((cell, i) => {
            return <Cell key={i} cell={cell} />;
          })}
      </div>
    );
  }
}
