import React, { Component } from 'react';
import axios from 'axios';

export default class SignUp extends Component {
	constructor() {
		super();
		this.state = {
			username: '',
			email: '',
			password: '',
      passwordcheck: ''
		};
  }

  componentDidMount() {
		axios
      .get(`${process.env.REACT_APP_BACKEND_URL}/api/User`)
			.then((apiRes) => {
        console.log(apiRes)
				this.setState({ users: apiRes.data });
			})
			.catch((apiErr) => {
				console.log(apiErr);
			});
	}

  handleChange = (e) => {
    console.log(e.target.value);
    this.setState({
      [e.target.name]: e.target.value // the value of the input
    })
  }

	handleFormSubmit = (e) => {
		e.preventDefault();
		this.setState({
			username: '',
			email: '',
			password: '',
			passwordcheck: ''
		});
  };

	render() {
		return (
			<div>
				<form onSubmit={this.handleFormSubmit} onChange={this.handleChange}>
					<label>Username:</label>
				{/* 	<input type="text" name="username" value={this.state.username} /> */}
					<input type="text" name="username" value="Dino" />

					<label>Email:</label>
					{/* <input type="text" name="email" value={this.state.email} /> */}
					<input type="text" name="email" value="dino@dino.com" />

					<label>Password:</label>
{/* 					<input type="password" name="password" value={this.state.password} /> */}
					<input type="password" name="password" value="dino" />

					<label>Confirm your password:</label>
			{/* 		<input type="password" name="passwordcheck" value={this.state.passwordcheck} /> */}
					<input type="password" name="passwordcheck" value="dino" />

					<input type="submit" value="Submit" />
				</form>
			</div>
		);
	}
}
