  }

  // let children = React.Children.map(this.props.children, (child, index) => {
  //     return React.cloneElement(child, {doRender: this.state[index]});
  // });

  render() {
    return (
      <div>
        {this.currentIndex === 0 && <IntroText1 text={this.state.texts[0]} />}
        {this.currentIndex === 1 && <IntroText1 text={this.state.texts[1]} />}
        {this.currentIndex === 2 && <IntroText1 text={this.state.texts[2]} />}
        {this.currentIndex === 3 && <IntroText1 text={this.state.texts[3]} />}
        {/* <IntroText2 />
