import React, { Component } from 'react';
import axios from 'axios';

export default class SignUp extends Component {
	constructor() {
		super();
		this.state = {
			username: 'gggg',
			email: 'jjj@g.com',
			password: 'toto',
			// passwordcheck: ''
		};
	}
	handleSubmit = (e) => {
		e.preventDefault();
		axios.post(`${process.env.REACT_APP_BACKEND_URL}/api/User`, this.state).then(dbRes => console.log(dbRes)).catch(dbErr => console.log(dbErr))
	}

	handleChange = (e) => {
		console.log(e.target.value);
		this.setState({
			[e.target.name]: e.target.value // the value of the input
		})
		console.log(this.state)
	}

	// handleFormSubmit = (e) => {
	// 	e.preventDefault();
	// 	this.setState({
	// 		username: '',
	// 		email: '',
	// 		password: '',
	// 		passwordcheck: ''
	// 	});
	// };


	render() {
		console.log(this.state)
		return (
			<div>
				<form onSubmit={this.handleSubmit} onChange={this.handleChange}>
					<label>Username:</label>
					{/* 	<input type="text" name="username" value={this.state.username} /> */}
					<input type="text" name="username" />

					<label>Email:</label>
					{/* <input type="text" name="email" value={this.state.email} /> */}
					<input type="text" name="email" />

					<label>Password:</label>
					{/* 					<input type="password" name="password" value={this.state.password} /> */}
					<input type="password" name="password" />

					<label>Confirm your password:</label>
					{/* 		<input type="password" name="passwordcheck" value={this.state.passwordcheck} /> */}
					<input type="password" name="passwordcheck" />

					<input type="submit" value="Submit" />
				</form>
			</div>
		);
	}
}
