import React, { Component } from "react";
import IntroText1 from "../components/IntroText1";
import IntroText2 from "../components/IntroText2";
import IntroText3 from "../components/IntroText3";
import IntroText4 from "../components/IntroText4";

export default class Intro extends Component {
  state = {
    currentIndex: 0,
    texts: [
      //   "Somewhere on earth, about 65.2 million years ago...",
      { duration: 1000, text: "foo" },
      { duration: 2000, text: "bar" },
      { duration: 3000, text: "baz" },
      { duration: 5000, text: "bim" }
    ]
  };
  getInitialState() {
    let state = {};
    React.Children.forEach(this.props.children, (child, index) => {
      state[index] = false;
    });
    return state;
  }

  componentDidMount() {
    setTimeout(() => {
      const intervarlId = setInterval(() => {
        const x = this.state.currentIndex + 1;
        if (x <= 3) this.setState({ currentIndex: x });
        else {
          clearInterval(intervarlId);
          this.props.history.push("/");
        }
      }, this.state.texts[this.state.currentIndex].duration);
    }, 5000);
    // this.timeouts = React.Children.forEach(
    //   this.props.children,
    //   (child, index) => {
    //     return setTimeout(() => {
    //       this.setState({ currentIndex: this.state.currentIndex + 1 });
    //     }, 5000);
    //   }
    // );
  }

  // let children = React.Children.map(this.props.children, (child, index) => {
  //     return React.cloneElement(child, {doRender: this.state[index]});
  // });

  render() {
    return (
      <div>
        {this.state.currentIndex === 0 && (
          <IntroText1 text={this.state.texts[0].text} />
        )}
        {this.state.currentIndex === 1 && (
          <IntroText1 text={this.state.texts[1].text} />
        )}
        {this.state.currentIndex === 2 && (
          <IntroText1 text={this.state.texts[2].text} />
        )}
        {this.state.currentIndex === 3 && (
          <IntroText1 text={this.state.texts[3].text} />
        )}
        {/* <IntroText2 />
        <IntroText3 />
        <IntroText4 /> */}
      </div>
    );
  }
}
