import React, { Component } from "react";
import axios from "axios";

export default class SignUp extends Component {
  constructor() {
    super();
    this.state = {
      username: "gggg",
      email: "jjj@g.com",
      password: "toto"
      // passwordcheck: ''
    };
  }
  handleSubmit = e => {
    e.preventDefault();
    axios
      .post(`${process.env.REACT_APP_BACKEND_URL}/api/User`, this.state)
      .then(dbRes => console.log(dbRes))
      .catch(dbErr => console.log(dbErr));
  };

  handleChange = e => {
    console.log(e.target.value);
    this.setState({
      [e.target.name]: e.target.value // the value of the input
    });
    console.log(this.state);
  };

  // handleFormSubmit = (e) => {
  // 	e.preventDefault();
  // 	this.setState({
  // 		username: '',
  // 		email: '',
  // 		password: '',
  // 		passwordcheck: ''
  // 	});
  // };

  render() {
    console.log(this.state);
    return (
      <div className="signContainer">
        <h1 className="signTitle">Sign Up</h1>
        <form onSubmit={this.handleSubmit} onChange={this.handleChange}>
          <div className="formContainer">
            <div className="boxSign">
              <label className="usernameLabel">Username:</label>
              {/* 	<input type="text" name="username" value={this.state.username} /> */}
              <input className="username" type="text" name="username" />
            </div>
            <div className="boxSign">
              <label className="emailLabel">Email:</label>
              {/* <input type="text" name="email" value={this.state.email} /> */}
              <input className="email" type="text" name="email" />
            </div>
            <div className="boxSign">
              <label className="passwordLabel">Password:</label>
              {/* 					<input type="password" name="password" value={this.state.password} /> */}
              <input className="password" type="password" name="password" />
            </div>
            <div className="boxSign">
              <label className="passwordLabel">Confirm your password:</label>
              {/* 		<input type="password" name="passwordcheck" value={this.state.passwordcheck} /> */}
              <input
                className="password"
                type="password"
                name="passwordcheck"
              />
            </div>
            <div className="boxButton">
              <input
                className="btnSign"
                type="submit"
                value="Submit"
                href="/menu"
              />
            </div>
          </div>
        </form>
      </div>
    );
  }
}
