import React, { Component } from "react";
import ReactTypingEffect from "react-typing-effect";

var counter = 10;
var intervalId = null;

function start() {
  intervalId = setInterval(textIntro, 1000);
}

function finish() {
  clearInterval(intervalId);
  document.getElementById("bip").innerHTML = "TERMINE!";
}

function textIntro() {
  counter--;
  if (counter == 0) finish();
  else {
    return (
      <ReactTypingEffect text="Somewhere on earth, about 65.2 milion years ago..." />
    );
  }
}

// const ReactTypingEffectDemo = () => {
//   return (
//     <ReactTypingEffect text="Somewhere on earth, about 65.2 milion years ago..." />
//   );
// };

export default class IntroText extends Component {
  render() {
    return (
      <div className="intro">
        <div className="textIntro">{start()}</div>
      </div>
    );
  }
}
